import java.io.*;
import java.util.*;
import com.eviware.soapui.model.testsuite.*
import com.eviware.soapui.impl.wsdl.teststeps.assertions.*
def tc = testRunner.testCase.testSuite.testCases["BookingAddOns_TestCase"];

def ts = testRunner.testCase.getTestStepByName("CruiseSearch")
def res = new XmlSlurper().parseText(ts.getPropertyValue("ResponseAsXml"));

def packageCode = res.resultsBySailing.sailingSummaryWithClassLevelPricing[3].sailingRef.@package.text();
testRunner.testCase.setPropertyValue("PackageCode",packageCode)

String shipCode= packageCode.substring(0,2);
testRunner.testCase.setPropertyValue("ShipCode",shipCode)

def sailDate = res.resultsBySailing.sailingSummaryWithClassLevelPricing[3].sailingRef.@saildate.text();
testRunner.testCase.setPropertyValue("SailDate",sailDate)

def id = res.resultsBySailing.sailingSummaryWithClassLevelPricing[3].cabinClassPricing[0].price[0].@priceId.text();
if(id.equals('')){
	def fareCode = res.resultsBySailing.sailingSummaryWithClassLevelPricing[3].cabinClassPricing[0].price[0].promotion.@id.text();
	testRunner.testCase.setPropertyValue("FareCode",fareCode)
}
else{
	testRunner.testCase.setPropertyValue("FareCode",id)
}

