import java.io.*;
import java.util.*;
import com.eviware.soapui.model.testsuite.*
import com.eviware.soapui.impl.wsdl.teststeps.assertions.*
def tc = testRunner.testCase.testSuite.testCases["ReleaseBooking_TestCase"];

def ts = testRunner.testCase.getTestStepByName("RetrieveBooking")
def res = new XmlSlurper().parseText(ts.getPropertyValue("ResponseAsXml"));

def packageCode = res.booking.bookingInformation.sailing.packageCode.text();
testRunner.testCase.setPropertyValue("PackageCode",packageCode)

def categoryCode = res.booking.bookingInformation.cabin.berthedCategoryCode.text();
testRunner.testCase.setPropertyValue("CategoryCode",categoryCode)

def fareCode = res.booking.bookingInformation.cabin.fareCode.text();
testRunner.testCase.setPropertyValue("FareCode",fareCode)

def cabinNumber = res.booking.bookingInformation.cabin.number.text();
testRunner.testCase.setPropertyValue("CabinNumber",cabinNumber)

def sitting = res.booking.diningSelection.e.sitting.text();
testRunner.testCase.setPropertyValue("Sitting",sitting)

def sittingTime = res.booking.diningSelection.e.sittingTime.text();
testRunner.testCase.setPropertyValue("SittingTime",sittingTime)

def sittingType = res.booking.diningSelection.e.sittingType.text();
testRunner.testCase.setPropertyValue("SittingType",sittingType)

def bookingId = res.booking.bookingInformation.bookingId.text();
testRunner.testCase.setPropertyValue("BookingId",bookingId)

def lastModified = res.booking.bookingInformation.lastModified.text();
testRunner.testCase.setPropertyValue("LastModified",lastModified)

def status = res.booking.bookingInformation.status.text();
testRunner.testCase.setPropertyValue("BookingStatus",status)

def name = res.booking.bookingSummary.guestNames[0].text();
def Name = name.indexOf(' ');
def lastname = name.substring(Name+1);
def firstName = name.substring(0, Name);
testRunner.testCase.setPropertyValue("Lastname",lastname);
testRunner.testCase.setPropertyValue("FirstName",firstName);

def accessToken = res.bookingAccessToken.text();
testRunner.testCase.setPropertyValue("BookingAccessToken",accessToken)

