def origionalBookingAmount = context.expand( '${Properties_Booking#OrigionalBookingAmount}' );
def bookingAmountAfterUpdate = context.expand( '${Properties_Booking#BookingAmountAfterUpdate}' );

//log.info (origionalBookingAmount)
//log.info (bookingAmountAfterUpdate)

//assert (bookingAmountAfterUpdate > origionalBookingAmount);


if (origionalBookingAmount < bookingAmountAfterUpdate)
{
	log.info ("Price Updated")
	assert true;
}else{
	//log.info ("Price DID NOT Update");
	testRunner.fail("Price DID NOT Update");
}
