//Set Test Case Cabin Number
def cabinNumber = res.cabin.e[0].cabinNumber.text()
	if (cabinNumber==''){
		log.info(cabinNumber)
		def gty = 'GTY'
		log.info(gty)
		testRunner.testCase.setPropertyValue("cabinNumber",gty);
