	def seatingTime  = res.diningOptions.traditionalDining.dining.e[i].seatingTime.text()
	log.info(seatingTime)
	testRunner.testCase.setPropertyValue("seatingTime",seatingTime);

}
else{
	log.info('Status Closed')
}
}

