import groovy.util.*


def ts = testRunner.testCase.getTestStepByName("retrieveBooking");

def xml = new XmlSlurper().parseText(ts.getPropertyValue("ResponseAsXml"));
log.info(xml)


//Set Test Case bookingAccessToken
