import java.io.*;
import java.util.*;
import com.eviware.soapui.model.testsuite.*;
import com.eviware.soapui.impl.wsdl.teststeps.assertions.*;
import groovy.json.*
import groovy.util.*

def ts = testRunner.testCase.getTestStepByName("retrieveBooking");
def retxml = new XmlSlurper().parseText(ts.getPropertyValue("ResponseAsXml"));

def ts2 = testRunner.testCase.getTestStepByName("updateBooking-add Guest");
def updxml2 = new XmlSlurper().parseText(ts2.getPropertyValue("ResponseAsXml"));

//Get BalanceDue in retrieveBooking
def retBalDue  = retxml.booking.bookingInformation.prices.priceInfo[0].e.amount[0].text()
log.info('Balance due before adding guest: ' + retBalDue)

//Get BalanceDue in updateBooking
def updBalDue  = updxml2.booking.bookingInformation.prices.priceInfo[0].e.amount[0].text()
log.info('Balance due after adding guest: ' + updBalDue)

//Get GrossTotal in retrieveBooking
def retGrossTotal  = retxml.booking.bookingInformation.prices.priceInfo[0].e.amount[2].text()
log.info('Gross Total before adding guest: ' + retGrossTotal)

//Get Gross Total in updateBooking
def updGrossTotal  = updxml2.booking.bookingInformation.prices.priceInfo[0].e.amount[2].text()
log.info('Gross Total after adding guest: ' + updGrossTotal)


if (updGrossTotal > retGrossTotal){
	log.info('Price changed increase from: '+retGrossTotal + ' to ' +updGrossTotal) 
	}
	 else {
		log.info('Price changed less from: '+retGrossTotal +' to ' +updGrossTotal)
}

import java.io.*;
import java.util.*;
import com.eviware.soapui.model.testsuite.*;
import com.eviware.soapui.impl.wsdl.teststeps.assertions.*;
import groovy.json.*
import groovy.util.*

def ts = testRunner.testCase.getTestStepByName("updateBooking-add Guest");
def retxml = new XmlSlurper().parseText(ts.getPropertyValue("ResponseAsXml"));

def ts2 = testRunner.testCase.getTestStepByName("updateBooking-remove Guest");
def updxml2 = new XmlSlurper().parseText(ts2.getPropertyValue("ResponseAsXml"));

//Get BalanceDue in Add Guest
def retBalDue  = retxml.booking.bookingInformation.prices.priceInfo[0].e.amount[0].text()
log.info('Balance due before removing guest: ' + retBalDue)

//Get BalanceDue in updateBooking
def updBalDue  = updxml2.booking.bookingInformation.prices.priceInfo[0].e.amount[0].text()
log.info('Balance due after removing guest: ' + updBalDue)

//Get GrossTotal in Add Guest
def retGrossTotal  = retxml.booking.bookingInformation.prices.priceInfo[0].e.amount[2].text()
log.info('Gross Total before removing guest: ' + retGrossTotal)

//Get BalanceDue in updateBooking
def updGrossTotal  = updxml2.booking.bookingInformation.prices.priceInfo[0].e.amount[2].text()
log.info('Gross Total after removing guest: ' + updGrossTotal)

//assert updGrossTotal == retGrossTotal

if (updGrossTotal > retGrossTotal){
	log.info('Price changed from: '+retGrossTotal + ' to ' +updGrossTotal) 
	}
	 else {
		log.info('Price changed less from: '+retGrossTotal +' to ' +updGrossTotal)
}
