import java.io.*;
import java.util.*;
import com.eviware.soapui.model.testsuite.*;
import com.eviware.soapui.impl.wsdl.teststeps.assertions.*;
import org.apache.commons.lang3.StringUtils;

def ts = testRunner.testCase.getTestStepByName("cruiseSearch-ROYAL");
String  url =  ts.getHttpRequest().getResponse().getURL(); 
log.info(url)

String brand = StringUtils.substringBetween(url, "criteria.brand.value=", "&");
log.info(brand)
testRunner.testCase.setPropertyValue("brand",brand);

String app = StringUtils.substringBetween(url, "header.application=", "&");
log.info(app)
testRunner.testCase.setPropertyValue("application",app);

String language = StringUtils.substringBetween(url, "header.language=", "&");
log.info(language)
testRunner.testCase.setPropertyValue("language",language);

String domainId = StringUtils.substringBetween(url, "header.domainId=", "&");
log.info(domainId)
testRunner.testCase.setPropertyValue("domainId",domainId);

String country = StringUtils.substringBetween(url, "criteria.country=", "&");
log.info(country)
testRunner.testCase.setPropertyValue("country",country);

String currency = StringUtils.substringBetween(url, "criteria.currency=", "&");
log.info(currency)
testRunner.testCase.setPropertyValue("currency",currency);

import java.io.*;
import java.util.*;
import com.eviware.soapui.model.testsuite.*;
import com.eviware.soapui.impl.wsdl.teststeps.assertions.*;


def ts = testRunner.testCase.getTestStepByName("cruiseSearch-ROYAL");
def res = new XmlSlurper().parseText(ts.getPropertyValue("ResponseAsXml"));

def packageCode = res.resultsByPackage.packageSummaryWithClassLevelPricing[0].packageReference.@code.text()
log.info(packageCode)
testRunner.testCase.setPropertyValue("packageCode",packageCode);

def  shipCode= packageCode.substring(0,2);
log.info(shipCode)
testRunner.testCase.setPropertyValue("shipCode",shipCode);

def sailDate = res.resultsByPackage.packageSummaryWithClassLevelPricing[0].cabinClassPriceBySailing[0].sailingRef.@saildate.text()
log.info(sailDate)
testRunner.testCase.setPropertyValue("sailDate",sailDate);

def celebrityBrand = "C"
def royalBrand = "R"

def celebrityShips = ['AX', 'AT', 'BY', 'CS', 'EC', 'EG', 'EQ', 'FL', 'IN', 'ML', 'RF', 'SI', 'SL', 'SM', 'XP', 'XO']
def royalShips = ['AD', 'AL', 'AN', 'BR', 'NE', 'EN', 'EX', 'FR', 'GR', 'HM', 'IC', 'ID', 'JW', 'LB', 'MJ', 'MA', 'NV', 'NC', 'OA', 'OY', 'OV', 'QN', 'RD', 'RH', 'SR', 'SC', 'SY', 'WC', 'UT', 'VI', 'VY', 'WN']


if (celebrityShips.contains(shipCode)) {	
	testRunner.testCase.setPropertyValue("brand",celebrityBrand);
	} else {
		testRunner.testCase.setPropertyValue("brand",royalBrand);
		}

con:configuration.scriptText:
def jsonRes = messageExchange.getResponse().contentAsString
def jsonSlurper = new groovy.json.JsonSlurper();
def parse = jsonSlurper.parseText(jsonRes)

def stateroomCategoryTypes = parse.stateroomCategoryTypes.stateroomType
assert stateroomCategoryTypes!=null

