con:config.script:
import java.io.*;
import java.util.*;
import com.eviware.soapui.model.testsuite.*;
import com.eviware.soapui.impl.wsdl.teststeps.assertions.*;


def ts = testRunner.testCase.getTestStepByName("RetrieveVanityUrl-C");
def res = new XmlSlurper().parseText(ts.getPropertyValue("ResponseAsXml"));

//Set Test Case vanityPath
def vanityPath = res.vanityUrl.vanityPath[0].text()
log.info(vanityPath)
testRunner.testCase.setPropertyValue("vanityPath",vanityPath);

